import React from 'react'
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
function Login(){

const [email, setEmail] = React.useState('');
const [pass, setPass] = React.useState('');
const navigate = useNavigate();

useEffect(() => {
  
    const auth = localStorage.getItem('user');

    if(auth) {navigate('/')}
}
,[])


const handleClick = async () => {
    let result = await fetch('http://localhost:5000/login', {
        method:'post',
        body:JSON.stringfy((email,pass)),
        headers:{
        'Content-Type': 'application/json'
        }
    });
    result = await result.json();

    if(result.email)
    {
        localStorage.setItem("user",JSON.stringify(result));
        navigate('/');
    }
    else{
        alert("please write correct")
    }
}



  return (
    <div className='login'>
        <h1 className='menu'>Login</h1>

            <input className='menu' type="text" name="" id="" 
            placeholder='Enter your Email'
            onChange={(e)=>{setEmail(e.target.value)}} value ={email} />

            <input className='menu' type="text" name="" id="" 
            placeholder='Enter your password'
            onChange={(e)=>{setPass(e.target.value)}} value = {pass}/>

            <button className='menu' onClick={handleClick}>Login</button>
    </div>
  );
}

export default Login;
