import React from 'react'
import { Link, useNavigate } from 'react-router-dom';
function Header() {

  const auth = localStorage.getItem('user');
  const navigate = useNavigate();

  const logout = () => {
    localStorage.clear();
    navigate('/signup');
  }

  return (
    <div className='header'>
      <ul className='hList'>
        {auth ? <>
          <li className='menu'><Link to='/'>Home </Link></li>
          <li className='menu'><Link to='/add'>Add</Link></li>
          <li className='menu'><Link to='/update'>Update</Link></li>
          <li className='menu'><Link to='/profile'>Profile</Link></li>
          <li style={{"marginLeft":"0px"}}>
          <li className='menu' style={{"color":"red", "fontWeight":"750"}}>{JSON.parse(auth).username}</li>
          <li className='false'><Link to='/signup' onClick={logout}>Logout</Link></li>
          </li>
        </>
          : <div className='false'>
            <li><Link to='/login'>Login</Link></li>
            <li><Link to='/signup'>Sign UP</Link></li>
          </div>
        }
      </ul>
    </div>
  )
}

export default Header
